import Bar from '../BaseCharts/Bar';
import reactiveProp from '../mixins/reactiveProp';
export default {
  extends: Bar,
  mixins: [reactiveProp],
  data: function data() {
    return {
      options: {
        responsive: true,
        maintainAspectRatio: false
      }
    };
  },
  mounted: function mounted() {
    this.renderChart(this.chartData, this.options);
  }
};